module.exports = function(dirname,fileType){
	var fs = require("fs")
	var arr = [],
			keys = [],
			obj = {}
	fs.readdirSync(dirname).forEach(function(filename){
		if(~filename.indexOf(fileType)){
			obj[filename.split(".")[0]] = require(dirname+"/"+filename);
			keys.push(filename.split(".")[0])
			arr.push(obj);
		}
	});
	return {
		keys:keys,
		models:arr
	};
}
