var fs = require("fs");

var mainPath = require("./mainPath.js"),
		manifest = mainPath("foxie")

function $class(){
	this.async = function(cb){
		fs.readFile(manifest+"/manifest.json",'utf8',function(err,data){
			cb(err,data)
		})
	}
	this.sync = function(){
		return require(manifest+"/manifest.json");
	}
}
var fn = new $class()
module.exports = fn;
