module.exports = function(mode){

	var foxiePathArr = require.main.filename.split("/"),
				last = foxiePathArr.indexOf(foxiePathArr.pop())
		delete foxiePathArr[last]
		var foxiePath = foxiePathArr.join("/")
	if(mode=="foxie"||!mode){
		return foxiePath+"/node_modules/foxie";
	}
	else if(mode=="server"){
		return foxiePath;
	}
	else{
		return foxiePath+mode;
	}
}
