var colors = require('colors/safe');

exports = module.exports = function($,proc,message){

	//INDEX ALL METHODS
	function all(){
		if(message) console.log(colors.red(message)) //its gone wrong, probably not a function
		console.log("Usage: <command>\n")
		console.log("Where <command> is one of:\n")
		for(command in $){
			console.log("\t"+command+"\t\t"+$[command].description)
		}
		console.log("\n")
		console.log(colors.green("Run <command> -h or --help for help with sub-commands"))
	}
	//SUB PROCCESS HELP
	function sub(){
		console.log(proc)
		console.log("Usage: "+proc+" <command>\n")
		console.log("Where <command> is one of:\n")
		var method = $[proc].method;
		for(detail in method){
			console.log("\t"+detail+"\t\t"+method[detail].help)
		}
		console.log("\t")
	}

	if(proc) sub()
	if(!proc) all()
	//if(proc)

}
