var prompt = require("prompt"),
		colors = require("colors")

prompt.start();


var schema = {
	properties:{
		host:{
			type:"string",
			required:true
		},
		port:{
			description:"mongo port",
			required:true,
		},
		database:{
			required:true,
			description:"database name"
		},
		username:{
			type:"string"
		},
		password:{
			type:"string"
		}
	}
}

//main call
function configure(){
	console.log("Starting database config".green)
	prompt.get(schema, function(err,result){
		console.log(result)
	});
}

function database(){
	this.configure = function(){
		configure()
	}
	//shorthand
	this["-c"] = this.configure
}
var database = new database()
module.exports = database;
