var sys = require('sys')
			var exec = require('child_process').exec;

function server(){
	this.start = function(){
			function puts(error, stdout, stderr) {sys.puts(stdout)}
			exec("npm faq", puts);
	}
	this.watch = function(){
		function puts(error, stdout, stderr) {sys.puts(stdout)}
		exec("nodemon ./server.js", puts);
	}
	this["-s"] = this.start;
	this["-w"] = this.watch;
}
var server = new server()
module.exports = server;
