var api = require("foxie-api"),
		schema = require("js-schema")

var post = {
	main:schema({
		title:String,
		description:String,
		body:String,
		url:String,
		comment:Object,
		author:String,
		postDate:String,
		published:Boolean
	}),
	comments:schema({
		author:String,
		body:String,
		postDate:String,
		registered:Boolean,
		role:String
	})
}

module.exports = post
