var data = require("foxie-data")

function $class(){
	this.create = function(mount,conf,verbose,cb){
		mount.get(conf.url, function(req, res) {
			if( !/*fake it*/ data.install_stat.core){//installed

				if(verbose){
					mount.set("json spaces",4)
					res.json(conf.data)
				}
				else{
					res.render("index",conf.data)
				}

			}
			else{//not installed

				res.send("Not istalled")

			}
		});

	//cb
	}
}
route = new $class()
module.exports = route;
