var jade = require("jade"),
		api = require("foxie-api"),
		api = {
			selectMount:api.router.select
		}

module.exports = function(themeName,assets,mountName){
	var assets = assets.settings,
			handlersDir = "./handlers"

	var mountPath = api.selectMount(mountName).mountpath

	require(handlersDir+"/libraries.js")(themeName,assets.libraries,mountPath)
	require(handlersDir+"/scripts.js")(themeName,assets.scripts,mountPath)
	require(handlersDir+"/styles.js")(themeName,assets.styles,mountPath)

}
