var api = require("foxie-api"),
		data = require("foxie-data"),
		mongoose = require("foxie-mongoose"),
		api = {
			loadModel:api.connection.loadModel,
			saveData:api.data.extend,
			selectMount:api.router.select
		}


module.exports = function(themeName,asset){

	api.saveData("_libraries",themeName,[])

	var models = {
		libraries:api.loadModel("foxie-libraries","libraries.model.js")
	}

	for(a in asset){
		models.libraries.find({library:asset[a]})
		.then(function(res){
			for(r in res){
				data._libraries[themeName].push(res[r].compile)
			}
		})
	}
	//console.log(themeName)
}
