var jade = require("jade"),
		path = require("path")

module.exports = function(themeName,asset,mountPath){

	var scripts = asset

	for(s in scripts){
		var urlScriptsPatern = path.join(mountPath,"themes",themeName,scripts[s])
		scripts[s] = jade.compile("script(src='"+urlScriptsPatern+"')")()
	}
}
