var jade = require("jade"),
		path = require("path")

module.exports = function (themeName, asset, mountPath) {
	var styles = asset
	for (s in styles) {
		var urlStylesPatern = path.join(mountPath,"themes",themeName,styles[s])
		console.log("what: ",styles[s])
		styles[s] = jade.compile("link(rel='stylesheet' type='text/css' href='"+urlStylesPatern+"')")()
	}
}
