var data = require("foxie-data"),
		api = require("foxie-api"),
		express = require("express"),
		stylus = require("stylus"),
		path = require("path"),
		api = {
			selectMount:api.router.select
		}

function unMachineName(str){
	return str.replace("_","-")
}

module.exports = function(thisMountTheme,mount){

	var publicThemePath = data.public.themes,
			themePath = publicThemePath+"/"+unMachineName(thisMountTheme),
			mount = api.selectMount(mount)

	var urlPaternThemes = path.join("/themes",thisMountTheme)
	var urlPaternLibraries = path.join("/libraries")

	mount.use(urlPaternThemes,express.static(themePath));
	mount.use(urlPaternLibraries,express.static(data.public.libraries))
}
