var path = require("path"),
		data = require("foxie-data"),
		fs = require("fs")

//blocking with fs

module.exports = function(themeName){
	var stat = fs.readdirSync(path.join(data.public.themes))
	try{
		if(stat.indexOf(themeName)>=0){
			return path.join(data.public.themes,themeName);
		}
	}
	catch(err){
		if(err) console.log("[ERROR] : Api -  There is no theme by the name "+themeName )
	}
}
