var data = require("foxie-data"),
		recurseDir = require("recursive-readdir")

module.exports = function(){

	var systemDefaultTemplates = data.themes.overide_dir+"/templates/"+data.themes.engine;

	//read system default templates
	recurseDir(systemDefaultTemplates,function(err,res){
		if(err) console.log(err)

		require("./systemManifest.js")(res)

	})


}
