var data = require("foxie-data"),
		api = require("foxie-api"),
		api = {
			newData:api.data.extend
		}

module.exports = function(systemTemplates){

	//we only need templates at this point in time!

	function closeMatch(iterate, ext) {
		var fileExt = "."+iterate.split(".").pop();
		if (fileExt == ext) return true;
		else return false;
	}

	var valid = []

	var res = systemTemplates;
	for(i in res){//sort into groups
		if(closeMatch(res[i],"."+data.themes.engine)) valid.push(res[i])
	}

	api.newData("temp","template",valid)
}
