var api = require("foxie-api"),
		path = require("path"),
		publicPath = require("foxie-data").public.path
		api.newData = api.data.extend


//template engine
api.newData("themes","engine","jade")
api.newData("themes","preprocessor","stylus")

//available themes
api.newData("themes","default",["foxes","foxes-admin"])

//mount theme settings
api.newData("themes_set","app","foxes")
api.newData("themes_set","admin","foxes-admin")
api.newData("themes_set","installer","foxes-admin")

//expected data after themes dir is parsed
api.newData("themes","ready",[])
api.newData("themes","dormant",[])

//system real view location
api.newData("themes","overide_dir",path.resolve(require.resolve("foxie-themes"),"../system"))

//themes path
api.newData("public","themes",publicPath+"/themes")
