//setup initial data Schema: for reference and pre install or default to.
var api = require("foxie-api"),
		path = require("path")
		api.newData = api.data.extend
		api.mainPath = api.path.mainPath
		api.publicPath = api.path.publicPath

//Install
api.newData("install_stat","core",false)

api.newData("public","path",api.mainPath("server")+"/public")

//Db Models
api.newData("data_model","core",path.resolve(require.resolve("foxie"),"../schema/data-model"))
api.newData("db_model","core",path.resolve(require.resolve("foxie"),"../schema/db-model"))

//Modules
api.newData("subcores","ready","")
api.newData("subcores","dormant","")

//common template data
api.newData("view_common","admin_menu","")

//Testing
api.newData("test_data","content","")
api.newData("test_data","blocks","")

//meta
api.newData("app_meta","name","Foxie")
api.newData("app_meta","description","This is one Foxie App!")
